package ch02.sec02;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// DataType
		// 기본타입 8개(정수/실수/문자/논리)
		// 정수 타입 : byte/short/int/long
		// 실수 타입 : float/double
		// 문자 타입 : char
		// 논리 타입 : boolean
		// 데이터 타입에 따라 사용하는 메모리 크기가 다름
		// 메모리 크기 최소 단위 : bit (0 또는 1)
		// bit가 8개 모여 byte 단위
		// byte : 데이터를 처리하는 최소 단위 -> 256개의 데이터 표현
		
		// 참조형 타입(객체) : String
		
	}

}
