package ch03.sec02;

public class AssignOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		result += 10;
		System.out.println("result = " + result);
		
		result -= 5;
		System.out.println("result = " + result);
		
		result *= 3;
		System.out.println("result = " + result);
		
		result /= 2;
		System.out.println("result = " + result);
		
		result %= 3;
		System.out.println("result = " + result);
	}

}
