package ch04.sec01;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 85;
		
		if(score >= 90)
			System.out.println("점수가 90점보다 크거나 같습니다.");
		else
			System.out.println("점수가 90점 미만입니다.");
		if(score >= 90)
			System.out.println("점수가 90점보다 크거나 같습니다.");
		if(score >= 90)
			System.out.println("점수가 90점보다 크거나 같습니다.");
	}

}
